// src/App.js
import { useState, useEffect } from 'react';
import axios from 'axios';

const API = "http://localhost:8000/quotes";

function App() {
  const [quotes, setQuotes] = useState([]);
  const [text, setText] = useState("");

  // Fetch quotes on load
  useEffect(() => {
    axios.get(API).then(res => {
      const data = res.data;
      const formatted = data.map((q, i) => ({ id: i, text: q }));
      setQuotes(formatted);
    });
  }, []);

  // Add a new quote
  const addQuote = async () => {
    if (!text.trim()) return;

    try {
      await axios.post(API, { quote: text });
      const updated = [...quotes, { id: quotes.length, text }];
      setQuotes(updated);
      setText("");
    } catch (err) {
      console.error("Add failed:", err);
    }
  };

  // Delete a quote by index
  const deleteQuote = async (id) => {
    try {
      await axios.delete(`${API}/${id}`);
      const updated = quotes.filter(q => q.id !== id).map((q, i) => ({ id: i, text: q.text }));
      setQuotes(updated);
    } catch (err) {
      console.error("Delete failed:", err);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>Quote Vault</h1>
      <input
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Enter a quote"
        style={{ marginRight: "1rem", padding: "0.5rem", width: "300px" }}
      />
      <button onClick={addQuote} style={{ padding: "0.5rem 1rem" }}>Add</button>

      <ul style={{ marginTop: "2rem", listStyle: "none", padding: 0 }}>
        {quotes.map(q => (
          <li key={q.id} style={{ marginBottom: "1rem" }}>
            “{q.text}”
            <button
              onClick={() => deleteQuote(q.id)}
              style={{ marginLeft: "1rem", color: "red", cursor: "pointer" }}
            >
              ❌
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
