// src/App.js
import { useState, useEffect } from 'react';
import axios from 'axios';

const API = "http://localhost:8000/quotes";

function App() {
  const [quotes, setQuotes] = useState([]);
  const [text, setText] = useState("");

  // Fetch quotes on load
  useEffect(() => {
    axios.get(API).then(res => {
      const data = res.data;
      const formatted = data.map((q, i) => ({ id: i, text: q }));
      setQuotes(formatted);
    });
  }, []);

  // Add a new quote
  const addQuote = async () => {
    if (!text.trim()) return;

    try {
      await axios.post(API, { quote: text });
      const updated = [...quotes, { id: quotes.length, text }];
      setQuotes(updated);
      setText("");
    } catch (err) {
      console.error("Add failed:", err);
    }
  };

  // Delete a quote by index
  const deleteQuote = async (id) => {
    try {
      await axios.delete(`${API}/${id}`);
      const updated = quotes.filter(q => q.id !== id).map((q, i) => ({ id: i, text: q.text }));
      setQuotes(updated);
    } catch (err) {
      console.error("Delete failed:", err);
    }
  };

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>Quote Vault</h1>
      <input
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Enter a quote"
        style={{ marginRight: "1rem", padding: "0.5rem", width: "300px" }}
      />
      <button onClick={addQuote} style={{ padding: "0.5rem 1rem" }}>Add</button>

      <ul style={{ marginTop: "2rem", listStyle: "none", padding: 0 }}>
        {quotes.map(q => (
          <li key={q.id} style={{ marginBottom: "1rem" }}>
            “{q.text}”
            <button
              onClick={() => deleteQuote(q.id)}
              style={{ marginLeft: "1rem", color: "red", cursor: "pointer" }}
            >
              ❌
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}
const styles = {
  container: {
    maxWidth: "600px",
    margin: "2rem auto",
    padding: "1rem",
    fontFamily: "Segoe UI, sans-serif",
    backgroundColor: "#f9f9f9",
    borderRadius: "8px",
    boxShadow: "0 0 10px rgba(0,0,0,0.1)",
  },
  title: {
    textAlign: "center",
    marginBottom: "1.5rem",
    color: "#333",
  },
  inputGroup: {
    display: "flex",
    gap: "1rem",
    marginBottom: "2rem",
  },
  input: {
    flex: 1,
    padding: "0.75rem",
    fontSize: "1rem",
    borderRadius: "6px",
    border: "1px solid #ccc",
  },
  button: {
    padding: "0.75rem 1.25rem",
    fontSize: "1rem",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    transition: "background-color 0.3s",
  },
  list: {
    listStyle: "none",
    padding: 0,
  },
  card: {
    backgroundColor: "#fff",
    padding: "1rem",
    marginBottom: "1rem",
    borderRadius: "6px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    boxShadow: "0 2px 5px rgba(0,0,0,0.05)",
  },
  quote: {
    fontSize: "1.1rem",
    color: "#444",
  },
  delete: {
    background: "none",
    border: "none",
    fontSize: "1.2rem",
    color: "#e74c3c",
    cursor: "pointer",
  },
};


export default App;
