// src/App.js
import { useState, useEffect } from 'react';
import axios from 'axios';

const API = "http://localhost:8000/quotes";

function App() {
  const [quotes, setQuotes] = useState([]);
  const [text, setText] = useState("");

  useEffect(() => {
    axios.get(API).then(res => setQuotes(res.data));
  }, []);

  const addQuote = async () => {
    const newQuote = { id: Date.now(), text };
    await axios.post(API, newQuote);
    setQuotes([...quotes, newQuote]);
    setText("");
  };

  const deleteQuote = async (id) => {
    await axios.delete(`${API}/${id}`);
    setQuotes(quotes.filter(q => q.id !== id));
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>Quote Vault</h1>
      <input
        value={text}
        onChange={e => setText(e.target.value)}
        placeholder="Enter a quote"
      />
      <button onClick={addQuote}>Add</button>
      <ul>
        {quotes.map(q => (
          <li key={q.id}>
            “{q.text}”
            <button onClick={() => deleteQuote(q.id)}>❌</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
